mapper.select:
select COUNT(*) AS prod_count from tbl_product P JOIN tbl_prod_img I ON P.pk_product_no = I.fk_product_no JOIN tbl_region R ON P.fk_region_no = R.pk_Region_No where I.prod_img_is_thumbnail = 1
and lower(P.product_title) like '%'||lower(#{search_prod})||'%'
and P.fk_category_no = #{pk_category_no}
and P.fk_category_detail_no = #{pk_category_detail_no}
order by P.product_regdate desc

SELECT TRUNC(AVG(TO_NUMBER(P.product_price))) AS avg_price, TRUNC(MAX(TO_NUMBER(P.product_price))) AS max_price, TRUNC(MIN(TO_NUMBER(P.product_price))) AS min_price FROM tbl_product P JOIN tbl_prod_img I ON P.pk_product_no = I.fk_product_no JOIN tbl_region R ON P.fk_region_no = R.pk_region_no WHERE I.prod_img_is_thumbnail = 1
and lower(P.product_title) like '%'||lower(#{search_prod})||'%'
and P.fk_category_no = #{pk_category_no}
and P.fk_category_detail_no = #{pk_category_detail_no}

select P.pk_product_no, P.fk_member_no, P.fk_region_no, P.fk_category_no, P.fk_category_detail_no, P.product_title, P.product_contents, P.product_price, P.product_status, P.product_sale_status, P.product_regdate, P.product_update_date, P.product_sale_type, P.product_views, I.pk_prod_img_no, I.prod_img_name, I.prod_img_org_name, I.prod_img_is_thumbnail, R.pk_region_no, R.region_state, R.region_city, R.region_town, R.region_lat, R.region_lng from tbl_product P JOIN tbl_prod_img I ON P.pk_product_no = I.fk_product_no JOIN tbl_region R ON P.fk_region_no = R.pk_Region_No where I.prod_img_is_thumbnail = 1
and lower(P.product_title) like '%'||lower(#{search_prod})||'%'
and P.fk_category_no = #{pk_category_no}
and P.fk_category_detail_no = #{pk_category_detail_no}
order by P.product_regdate desc
